package com.github.senocak

import com.github.senocak.domain.Friend
import com.github.senocak.domain.Message
import com.github.senocak.domain.Role
import com.github.senocak.domain.User
import com.github.senocak.repository.FriendRepository
import com.github.senocak.repository.MessageRepository
import com.github.senocak.repository.RoleRepository
import com.github.senocak.repository.UserRepository
import com.github.senocak.util.FriendShipStatus
import com.github.senocak.util.RoleName
import java.util.Base64
import java.util.Date
import java.util.concurrent.TimeUnit
import kotlin.random.Random
import net.datafaker.Faker
import org.slf4j.Logger
import org.slf4j.LoggerFactory
import org.springframework.beans.factory.annotation.Value
import org.springframework.boot.autoconfigure.SpringBootApplication
import org.springframework.boot.context.event.ApplicationReadyEvent
import org.springframework.boot.runApplication
import org.springframework.context.event.EventListener
import org.springframework.security.crypto.password.PasswordEncoder
import org.springframework.transaction.annotation.Transactional


@SpringBootApplication
class ChatApplication(
    private val roleRepository: RoleRepository,
    private val userRepository: UserRepository,
    private val passwordEncoder: PasswordEncoder,
    private val messageRepository: MessageRepository,
    private val friendRepository: FriendRepository,
){
    private val log: Logger = LoggerFactory.getLogger(this.javaClass)
    private val faker = Faker()

    @Value("\${spring.jpa.hibernate.ddl-auto}")
    lateinit var ddl: String

    @Transactional
    @EventListener(value = [ApplicationReadyEvent::class])
    fun init(event: ApplicationReadyEvent): Unit =
        log.debug("[ApplicationReadyEvent]: ${event.timeTaken.toSeconds()}")
            .also {
                if (ddl == "create" || ddl == "create-drop") {
                    roleRepository.deleteAll()
                    userRepository.deleteAll()
                    messageRepository.deleteAll()
                    val userRole: Role = roleRepository.save(Role(name = RoleName.ROLE_USER))
                    val adminRole: Role = roleRepository.save(Role(name = RoleName.ROLE_ADMIN))
                    val defaultPass: String = passwordEncoder.encode("asenocak")
                    val user1: User = userRepository.save(User(name = "Anıl Şenocak", email = "anil@senocak.com", password = defaultPass, roles = arrayListOf(userRole, adminRole), picture = "data:image/png;base64,"))
                    val user2: User = userRepository.save(User(name = "Canela Skin", email = "canela@skin.com", password = defaultPass, roles = arrayListOf(userRole), picture = "data:image/png;base64,"))
                    val user3: User = userRepository.save(User(name = "Leah Gotti", email = "leah@gotti.com", password = defaultPass, roles = arrayListOf(adminRole), picture = "data:image/png;base64,"))
                    val user4: User = userRepository.save(User(name = "Sarah Banks", email = "sarah@banks.com", password = defaultPass, roles = arrayListOf(userRole), picture = "data:image/png;base64,"))
                    val user5: User = userRepository.save(User(name = "Raylin Joy", email = "raylin@joy.com", password = defaultPass, roles = arrayListOf(userRole), picture = "data:image/png;base64,"))
                    val user6: User = userRepository.save(User(name = "Samantha Saint", email = "samantha@saint.com", password = defaultPass, roles = arrayListOf(userRole), picture = "data:image/png;base64,"))

                    val friendShipRequest14: Friend = friendRepository.save(Friend(owner = user1, person = user4, status = FriendShipStatus.Accepted, approvedAt = Date()))
                    val friendShipRequest15: Friend = friendRepository.save(Friend(owner = user1, person = user5, status = FriendShipStatus.Accepted, approvedAt = Date(), blockedAt = Date(), blockedBy = user5))
                    val friendShipRequest21: Friend = friendRepository.save(Friend(owner = user2, person = user1, status = FriendShipStatus.Pending, blockedAt = Date(), blockedBy = user1))
                    val friendShipRequest31: Friend = friendRepository.save(Friend(owner = user3, person = user1, status = FriendShipStatus.Pending))
                    val friendShipRequest35: Friend = friendRepository.save(Friend(owner = user3, person = user5, status = FriendShipStatus.Pending))
                    val friendShipRequest36: Friend = friendRepository.save(Friend(owner = user3, person = user6, status = FriendShipStatus.Accepted, approvedAt = Date()))

                    val userRange: ArrayList<User> = arrayListOf(user1, user2, user3, user4, user5, user6)
                    val file1 = "data:image/png;base64,"
                    val file2 = "data:image/png;base64,"
                    val file3 = "data:image/png;base64,"
                    val file4 = "data:image/png;base64,"
                    val file5 = "data:@file/octet-stream;base64,"
                    val file6 = "data:@file/x-c++;base64,"

                    val fileRange: ArrayList<String> = arrayListOf(file1, file2, file3, file4, file5, file6)
                    for (nums: Int in 0..1_000) {
                        val randomUser: Pair<User, User> = randomUser(userRange)
                        val message = Message(from = randomUser.first, to = randomUser.second)
                        when {
                            Random.nextBoolean() -> message.readAt = faker.date().past(1, TimeUnit.DAYS)
                        }
                        when {
                            (0..10).random() != 5 -> message.text = faker.lorem().paragraph(2)
                            else -> message.binary = fileRange.random()
                        }
                        messageRepository.save(message)
                    }

                    //val msg231: Message = messageRepository.save(Message(from = user2, to = user3, text = "anil2 > anil3 1"))
                    //val msg232: Message = messageRepository.save(Message(from = user2, to = user3, text = "anil2 > anil3 2", readAt = Date()))
                    //val msg233: Message = messageRepository.save(Message(from = user2, to = user3, text = "anil2 > anil3 3", readAt = Date()))
                    //val msg234: Message = messageRepository.save(Message(from = user2, to = user3, text = "anil2 > anil3 4", readAt = Date()))
                    //val msg235: Message = messageRepository.save(Message(from = user2, to = user3, text = "anil2 > anil3 5", readAt = Date()))
                    //val msg236: Message = messageRepository.save(Message(from = user2, to = user3, binary = "anil2 > anil3 binary"))
                    log.debug("[ApplicationReadyEvent]: db migrated.")
                }
            }

    fun randomUser(userRange: ArrayList<User>): Pair<User, User> {
        val pair: Pair<User, User> = Pair(first = userRange.random(), second = userRange.random())
        if (pair.first == pair.second) {
            return randomUser(userRange = userRange)
        }
        return pair
    }
}

fun main(args: Array<String>) {
    runApplication<ChatApplication>(*args)
}
